#!/usr/bin/python
from Bio.SeqRecord import SeqRecord
from Bio import SeqIO
from Bio.Seq import Seq

aox = ''
sdh = ''
ten1 = ''
cox6 = ''
result_seqs = []
result_seqs.append(SeqRecord(seq=Seq(aox), id='AOX_Dpapi', description=''))
result_seqs.append(SeqRecord(seq=Seq(sdh), id='SDH_Dpapi', description=''))
result_seqs.append(SeqRecord(seq=Seq(ten1), id='TEN1_Dpapi', description=''))
result_seqs.append(SeqRecord(seq=Seq(cox6), id='COX6_Dpapi', description=''))

out_file = '/home/anna/Dropbox/phd/mitoproteomes/proteomes/Dpapi/dpapi_genes.fasta'
SeqIO.write(result_seqs, out_file, "fasta")